/*
 * Hook type spawning
 */

var Hook = require('../../lib/hookio').Hook;


var hook = new Hook({
  name: "a",
  debug: true
});

hook.on('hook::ready', function () {

  hook.spawn([
     {
       appsrc: '../../test/fixtures/helloworld/bin/server',
       name: 'b',
       foo: "bar"
     }
   ]);

});

hook.start();